var BuyCraftQueue = function(api) {

    this.api = api;

}

BuyCraftQueue.prototype.getPendingPlayers = function(callback) {

    this.api.get('queue', callback);

}

BuyCraftQueue.prototype.getOfflineCommands = function(callback) {

    this.api.get('queue/offline-commands', callback);

}

BuyCraftQueue.prototype.getOnlineCommands = function(player, callback) {

    this.api.get('queue/online-commands/'+player, callback);

}

BuyCraftQueue.prototype.deleteCommands = function(list, callback) {

    if (!Array.isArray(list)) {
        list = [list];}

    var query = "";

    for (var i = 0; i < list.length; i++) {
        query += "ids[]=" + list[i] + "&";}
    query = query.substring(0, query.length-1);

    this.api.del('queue', query, callback);

}

module.exports = BuyCraftQueue;
