var BuyCraftStore = function(api) {

    this.api = api;

}

BuyCraftStore.prototype.getInformations = function(callback) {

    this.api.get('information', callback);

}

BuyCraftStore.prototype.getListing = function(callback) {

    this.api.get('listing', callback);

}

BuyCraftStore.prototype.getPayments = function(options, callback) {

    if (!callback) {callback = options; options = null;}

    if (options) {

        var limit = options.limit || 25;
        
        this.api.get('payments?limit='+limit, callback);

    } else {

        this.api.get('payments', callback);

    }

}

BuyCraftStore.prototype.createCheckout = function(user, pack, callback) {

    this.api.post('checkout', {username: user, package_id: pack}, callback);

}

module.exports = BuyCraftStore;
